import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import {Divider, Avatar, Container} from '@material-ui/core';
import { deepOrange } from '@material-ui/core/colors';


const useStyles = makeStyles(theme => ({
    dividerStyle: {
        marginBottom: '1rem',
        marginTop: '1rem'
    },
    headerStyles: {
        fontSize: '1.7rem',
        marginLeft: '-3%'
    },
    paragraphStyles: {
        marginBottom: '1rem'
    },
    avatarStyles: {
        // flex: '1',
        fontSize: '1.5rem',
        marginLeft: 'auto',
        color: theme.palette.getContrastText(deepOrange[500]),
        backgroundColor: deepOrange[500]
    },
    inlineFlexContent: {
        display: "flex"
    },
    media: {
        height: 140
    }
  }));

const cardInfo = [
    {
        projectName: 'G20 Text Analysis',
        projectClass: 'D',
        projectDescription: "This was an extremely interesting project that involved the use of Python's amazing Pandas framework. The goal of this project was to analyse what was going on during the G20 world summit. It was easy and very possible to to get the general idea of the topics that were discussed by reading one or two news articles but how about thousands of articles? This is where it gets intersting...",
        imageUrl: '/cardImages/scott-graham-5fNmWej4tAA-unsplash'
    }
]

const ProjectCard = () => {
    const classes = useStyles();
    return (
        <Card>
            <CardMedia className={classes.media} image="/../cardImages/scott-graham-5fNmWej4tAA-unsplash" title="G20IMG"/>
            <CardContent>
                <Container className={classes.inlineFlexContent}>
                    <Typography className={classes.headerStyles} variant="h4">
                        G20 Text Analysis
                    </Typography>
                    <Avatar className={classes.avatarStyles}>D</Avatar>
                </Container>
            <Divider className={classes.dividerStyle}/>

            <Typography variant="h6" component="h2" className={classes.paragraphStyles}>
                What were the goals of this project?
            </Typography>
            <Typography className ={classes.paragraphStyles}>
                There were many ways of understanding news articles, one could be simply reading newspapers, blogging about it online or even talking about it with your peers. What we wanted to do was the analyse thousands of different articles from different sources and this was when programming really came in handy.
            </Typography>



            {/* <Typography>
                If you're interested to find out more about the success and flow of this project, hit me up at my contacts page!
            </Typography> */}
            </CardContent>
            <CardActions>
            <Button size="small">Learn More</Button>
            </CardActions>
        </Card>
    )
}

export default ProjectCard;
