import React from 'react';
import CssBaseline from '@material-ui/core/CssBaseline';
import Navbar from '../navbar/navbar';
import About from '../content/About';
import Particles from 'react-particles-js';
import {makeStyles} from '@material-ui/core/styles';
import './App.css';
const useStyles = makeStyles({
  particlesCanva: {
    position:'absolute',
    opacity: '0.3'
  }
})

const App = () => {
  const classes = useStyles();
  return (
    <>
      <CssBaseline/>

      <Navbar/>
      <About/>
      <Particles
      canvasClassName={classes.particlesCanva}
      params={{
        particles: {
          number:{
            value: 50,
            density: {
              enable: true,
              value_area: 900
            }
          },
          line_linked: {
            shadow: {
              enable: true,
              color: "#3CA9D1",
              blur: 5
            }
          },
          shape: {
            type: 'circle',
            stroke: {
              width: 1,
              color: '#d35400'
            }
          },
          size: {
            value: 8,
            random: true,
            anim: {
              enable: true,
              speed: 5,
              size_min: 0.1,
              sync: true
            }
          },
          opacity: {
            value: 1,
            random: true,
            anim: {
              enable: true,
              speed: 1,
              opacity_min: 0.1,
              sync: true
            }
          }
        }
      }
    }
      />  
    </>
  );
}

export default App;
