import React, {Component} from 'react';
import {withStyles, makeStyles} from '@material-ui/core/styles';
import {TextField, Button} from '@material-ui/core';
import AddCircleIcon from '@material-ui/icons/AddCircle';



const useStyles = makeStyles((theme) => ({
    addTeamMemberButton: {
        margin: '1rem 0.5rem',
        '&:hover': {
            backgroundColor: '#34495e',
            color: 'white'
        }
    },
    addTeamMemberButtonIcon: {
        marginLeft: '1rem'
    }
}));

const InputField = withStyles({
    root: {
        '& label.Mui-focused': {
            color: 'tomato',
        },
        '& label': {
            color: 'tan'
        },
        '& .MuiOutlinedInput-root': {
            '& fieldset': {
                borderColor: 'tan'
            },
            '&:hover fieldset': {
                borderColor: 'tan'
            },
            '& .Mui-focused fieldset': {
                borderColor: 'tan'
            }
        },
    },
})(TextField);

class ListBuilder extends Component {
    state= {
        names: ['Alton', 'Gladys']
    }

    addPerson() {
        this.setState({names: [...this.state.names, ' ']})
    }
    handleChange(e, index) {
        this.state.names[index] = e.target.value
        this.setState({names: this.state.names})

    }
    render() {
        // const classes = useStyles();
        return (
            <div>
                <h1>Names list</h1>
                <ul>
                    {this.state.names.map((person, index)=> {
                        return(
                            <div key={index}>
                                <input value={person} onChange={(e) => this.handleChange(e, index)}></input>
                                <li key={index}>Person's name is {person}</li>
                            </div> 
                        ) 
                    })}
                    <button onClick={(e) => this.addPerson(e)}>Add person</button>
                </ul>
            </div>
        )
    }
}
export default ListBuilder;
