import React from 'react';
import {Typography, Avatar, Grid, Box} from '@material-ui/core';
import {makeStyles} from '@material-ui/core/styles'
import avatar from './img/robbie.png';
//This creates an interesting typing animation from Typed.
import Typed from 'react-typed';

//CSS Styles 
const useStyles = makeStyles(theme => ({
    avatar: {
        width: theme.spacing(15),
        height: theme.spacing(15),
        margin: theme.spacing(1),
    },
    title: {
        color: "#16a085"
    },
    subtitle: {
        color: '#7f8c8d'
    }
}))

const About = () => {
    const classes = useStyles()
    return (
        <Box>
            <Avatar className={classes.avatar} src={avatar} alt="Alton Ong"/>
            <Typography className={classes.title} variant="h4">
                <Typed strings={['Alton Ong']} typeSpeed={40}/>
            </Typography>
            <br/>
            <Typography className={classes.subtitle} variant="h5">
                <Typed strings={['Web Design', 'Data Analytics', 'Animations']} 
                typeSpeed={40}
                backSpeed={60}
                loop
                />
            </Typography>
        </Box>
    )
}

export default About;
