import React, {Component} from 'react';
import {withStyles, makeStyles} from '@material-ui/core/styles';
import {TextField, Button} from '@material-ui/core';
import AddCircleIcon from '@material-ui/icons/AddCircle';



const useStyles = makeStyles((theme) => ({
    addTeamMemberButton: {
        margin: '1rem 0.5rem',
        '&:hover': {
            backgroundColor: '#34495e',
            color: 'white'
        }
    },
    addTeamMemberButtonIcon: {
        marginLeft: '1rem'
    }
}));

const InputField = withStyles({
    root: {
        '& label.Mui-focused': {
            color: 'tomato',
        },
        '& label': {
            color: 'tan'
        },
        '& .MuiOutlinedInput-root': {
            '& fieldset': {
                borderColor: 'tan'
            },
            '&:hover fieldset': {
                borderColor: 'tan'
            },
            '& .Mui-focused fieldset': {
                borderColor: 'tan'
            }
        },
    },
})(TextField);

class ListBuilder extends Component {
    state= {
        people: [
            {
                name: 'Alton',
            }
        ]
    }
    handleAddPerson = (e) => {
        const {name, value} = e.target;
        this.setState({
            ...this.state,
            [name]: value 
        })
    }
    render() {
        // const classes = useStyles();
        const {people} = this.state
        return (
            <div>
                <h1>Names list</h1>
                <ul>
                    {people.map((person)=> {
                        return <li key={person.id}>Person's name is {person.name}</li>
                    })}
                </ul>
                <InputField 
                    label='Name' 
                    fullWidth={true} 
                    variant='outlined' 
                    size='medium' 
                    onChange={this.handleAddPerson}
                    />   
                <Button>Add Team Member<AddCircleIcon /></Button>
            </div>
        )
    }
}
export default ListBuilder;
