//import actions
import * as Types from './types';

// Initial state
const initState = {
    searchText: "",
    country: "",
    category: "",
    searchResults: null,
    homeResults: []
}

// Reducer
const SearchReducer = (state = initState, { type, payload }) => {
    switch (type) {
        // Sets the values for the search
        case Types.INCREMENT: {
            return { ...state, searchText: payload }
        }
        default: {
            return state
        }
    }
}

export default SearchReducer
